mapper.select:
select no, userid, subject, content, hit, regtime
from announce 
where no = #{no}

mapper.insert:
insert into announce(
	userid, subject, content, regtime
) values (
	#{userid}, #{subject}, #{content}, now()
)

mapper.update:
update announce 
   set subject = #{subject}, 
   	   content = #{content}
 where no = #{no}

update announce
set hit = hit + 1
where no = #{no}
